custom_css:
.{{class_name}} .btn_2:hover {
    background-color: #e62727;
    }
.{{class_name}} .btn_1:hover {
    border-color: #e62727;
    color: #e62727;
    }

.{{class_name}} .btn_1 {
        font-family: 'Roboto', sans-serif;
        color: #ffffff;
        font-weight: 400;
        padding-top: 10px;
        padding-right: 10px;
        padding-bottom: 10px;
        padding-left: 10px;
        border-width: 1px;
        border-style: Solid;
        border-color: #ffffff;
        border-top-left-radius: 8px;
        border-bottom-left-radius: 8px;
        border-top-right-radius: 8px;
        border-bottom-right-radius: 8px;
}

.{{class_name}} .btn_2 {
            background-color: #CB2929;
            font-family: 'Roboto', sans-serif;
            color: #ffffff;
            font-weight: 400;
            padding-top: 10px;
            padding-right: 10px;
            padding-bottom: 10px;
            padding-left: 10px;
            border-width: 1px;
            border-style: Solid;
            border-color: #CB2929;
            border-top-left-radius: 8px;
            border-bottom-left-radius: 8px;
            border-top-right-radius: 8px;
            border-bottom-right-radius: 8px;
}

@media (max-width: 575px) {
    .{{class_name}} .btn_1, .{{class_name}} .btn_2 {
        font-size:12px;
    }
}

@media (min-width: 768px) {
    .{{class_name}} .btn_1, .{{class_name}} .btn_2 {
        font-size:16px;
    }
}

@media (min-width: 992px) {
    .{{class_name}} .btn_1, .{{class_name}} .btn_2 {
        font-size:18px;
    }
}

