custom_css:
.container_column .header__ {
    padding: 0 0 20px;
}
.container_column .header__ .subtitle {
    font-size: 38px;
    font-weight: 600;
    padding: 5px 0 0;
    text-align: center;
}
.container_column .header__ .title {
    font-size: 18px;
    font-weight: 400;
    color: var(--secondary-bg);
    text-align: center;
}
.container_column .box__1 {
    align-items: unset!important;
    padding: 20px 0;
}
.container_column .box__1 .image {
    flex: 0 0 50%;
}
.container_column .box__1 .empty_div {
    flex: 0 0 50%;
    position: relative;
}
.container_column .box__1 .content {
    height: 280px;
    padding: 10px 20px;
    background: #fff;
    box-shadow: 0 10px 30px -10px #97a3b880;
    position: absolute;
    border-radius: 10px;
    top: 18%;
}
.container_column .box__1 .content .content__box .content_value{
    align-items: center;
    width: 100%;
}
.container_column .box__1 .content .content__box .content_value img {
    height: 20px;
    width: unset;
    padding: 0 10px 0 0;
}
.container_column .box__1 .content .content__box .content_value .head_title {
    color: var(--secondary-bg);
    font-size: 23px;
}
.container_column .box__1 .content .content__box .para {
    color: var(--secondary-bg);
    font-size: 16px;
    padding: 10px 0;
}
.container_column .box__1 .content .content__box {
    flex-direction: column;
    justify-content: center;
    height: 100%;
}
.container_column .box__1 .image img {
    border-radius: 10px;
}

custom_js:
$('#testimonial1').owlCarousel({
    loop:true,
    margin:10,
    nav:false,
    dots: true,
    autoplay:true,
    autoplayTimeout:3000,
    autoplayHoverPause:false,
    responsive:{
        0:{
            items:1
        },
        600:{
            items:1
        },
        1000:{
            items:3
        }
    }
})
